import React, { useState, Fragment } from "react";
import {
  DragDropContext,
  Droppable,
  Draggable,
} from "react-beautiful-dnd";
import { Dialog, Transition } from "@headlessui/react";
import { PlusIcon } from "@heroicons/react/24/solid";

const Sidebar = () => (
  <div className="h-screen w-64 bg-gray-800 text-white flex flex-col p-4 space-y-4">
    <h2 className="text-xl font-bold mb-6">ðŸŸ£ ClickUp Clone</h2>
    <nav className="flex flex-col gap-3">
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Dashboard</a>
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Tasks</a>
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Calendar</a>
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Settings</a>
    </nav>
  </div>
);

const initialProjects = {
  ProjectA: {
    todo: [{ id: "1", content: "Initial setup" }],
    inprogress: [],
    done: [],
  },
  ProjectB: {
    todo: [],
    inprogress: [],
    done: [],
  },
};

const App = () => {
  const [projects, setProjects] = useState(initialProjects);
  const [activeProject, setActiveProject] = useState("ProjectA");
  const [isOpen, setIsOpen] = useState(false);
  const [newTaskText, setNewTaskText] = useState("");
  const [activeColumn, setActiveColumn] = useState("todo");
  const [editingTask, setEditingTask] = useState(null);

  const tasks = projects[activeProject];

  const switchProject = (e) => setActiveProject(e.target.value);

  const openModal = (columnId, task = null) => {
    setActiveColumn(columnId);
    setIsOpen(true);
    if (task) {
      setEditingTask(task);
      setNewTaskText(task.content);
    } else {
      setEditingTask(null);
      setNewTaskText("");
    }
  };

  const closeModal = () => {
    setNewTaskText("");
    setIsOpen(false);
  };

  const addTask = () => {
    if (!newTaskText.trim()) return;

    const newProjects = { ...projects };
    const column = newProjects[activeProject][activeColumn];

    if (editingTask) {
      const updated = column.map((t) =>
        t.id === editingTask.id ? { ...t, content: newTaskText } : t
      );
      newProjects[activeProject][activeColumn] = updated;
    } else {
      const newTask = { id: Date.now().toString(), content: newTaskText };
      column.push(newTask);
    }

    setProjects(newProjects);
    closeModal();
  };

  const deleteTask = (columnId, taskId) => {
    const updatedTasks = projects[activeProject][columnId].filter(
      (t) => t.id !== taskId
    );
    const updatedProject = {
      ...projects[activeProject],
      [columnId]: updatedTasks,
    };
    setProjects({ ...projects, [activeProject]: updatedProject });
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const sourceCol = source.droppableId;
    const destCol = destination.droppableId;

    const sourceTasks = [...projects[activeProject][sourceCol]];
    const destTasks = [...projects[activeProject][destCol]];
    const [moved] = sourceTasks.splice(source.index, 1);

    if (sourceCol === destCol) {
      sourceTasks.splice(destination.index, 0, moved);
    } else {
      destTasks.splice(destination.index, 0, moved);
    }

    const updatedProject = {
      ...projects[activeProject],
      [sourceCol]: sourceTasks,
      [destCol]: destTasks,
    };

    setProjects((prev) => ({
      ...prev,
      [activeProject]: updatedProject,
    }));
  };

  const addNewProject = () => {
    const newName = prompt("Enter new project name:");
    if (!newName || projects[newName]) return;
    setProjects({
      ...projects,
      [newName]: { todo: [], inprogress: [], done: [] },
    });
    setActiveProject(newName);
  };

  return (
    <div className="flex min-h-screen bg-gray-900 text-white">
      <Sidebar />
      <div className="flex-1 p-8 overflow-x-auto">
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">ðŸ—‚ Kanban Board</h1>
          <div className="flex gap-4 items-center">
            <select
              value={activeProject}
              onChange={switchProject}
              className="bg-gray-800 text-white border border-gray-600 rounded p-2"
            >
              {Object.keys(projects).map((proj) => (
                <option key={proj} value={proj}>
                  {proj}
                </option>
              ))}
            </select>
            <button
              onClick={addNewProject}
              className="bg-purple-600 hover:bg-purple-500 px-3 py-2 rounded"
            >
              + New
            </button>
          </div>
        </div>

        <DragDropContext onDragEnd={onDragEnd}>
          <div className="grid grid-cols-3 gap-6">
            {Object.entries(tasks).map(([columnId, columnTasks]) => (
              <Droppable key={columnId} droppableId={columnId}>
                {(provided) => (
                  <div
                    {...provided.droppableProps}
                    ref={provided.innerRef}
                    className="bg-gray-800 rounded-lg p-4 min-h-[300px] flex flex-col"
                  >
                    <div className="flex items-center justify-between mb-4">
                      <h2 className="text-lg font-semibold capitalize">
                        {columnId}
                      </h2>
                      <button
                        onClick={() => openModal(columnId)}
                        className="bg-gray-700 hover:bg-gray-600 p-1 rounded"
                      >
                        <PlusIcon className="w-5 h-5 text-white" />
                      </button>
                    </div>
                    {columnTasks.map((task, index) => (
                      <Draggable
                        key={task.id}
                        draggableId={task.id}
                        index={index}
                      >
                        {(provided) => (
                          <div
                            ref={provided.innerRef}
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            className="bg-gray-700 p-3 mb-3 rounded shadow hover:bg-gray-600 relative"
                          >
                            <div>{task.content}</div>
                            <div className="absolute top-1 right-1 flex gap-1 text-xs">
                              <button
                                onClick={() => openModal(columnId, task)}
                                className="text-yellow-300 hover:underline"
                              >
                                Edit
                              </button>
                              <button
                                onClick={() => deleteTask(columnId, task.id)}
                                className="text-red-400 hover:underline"
                              >
                                Delete
                              </button>
                            </div>
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </DragDropContext>
      </div>

      <Transition appear show={isOpen} as={Fragment}>
        <Dialog as="div" className="relative z-10" onClose={closeModal}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-200"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-150"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black/60" />
          </Transition.Child>

          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex min-h-full items-center justify-center p-6">
              <Dialog.Panel className="w-full max-w-md transform overflow-hidden rounded bg-gray-800 text-white p-6 shadow-xl">
                <Dialog.Title className="text-lg font-bold mb-4">
                  {editingTask ? "Edit Task" : "Add Task"} to{" "}
                  <span className="capitalize">{activeColumn}</span>
                </Dialog.Title>
                <input
                  type="text"
                  className="w-full p-2 rounded bg-gray-700 border border-gray-600 mb-4"
                  placeholder="Task name..."
                  value={newTaskText}
                  onChange={(e) => setNewTaskText(e.target.value)}
                />
                <div className="flex justify-end gap-2">
                  <button
                    onClick={closeModal}
                    className="px-4 py-2 rounded bg-gray-600 hover:bg-gray-500"
                  >
                    Cancel
                  </button>
                  <button
                    onClick={addTask}
                    className="px-4 py-2 rounded bg-purple-600 hover:bg-purple-500"
                  >
                    {editingTask ? "Save" : "Add"}
                  </button>
                </div>
              </Dialog.Panel>
            </div>
          </div>
        </Dialog>
      </Transition>
    </div>
  );
};

export default App;
