import React, { useState, Fragment } from "react";
import {
  DragDropContext,
  Droppable,
  Draggable,
} from "react-beautiful-dnd";
import { Dialog, Transition } from "@headlessui/react";
import { PlusIcon } from "@heroicons/react/24/solid";

const Sidebar = () => (
  <div className="h-screen w-64 bg-gray-800 text-white flex flex-col p-4 space-y-4">
    <h2 className="text-xl font-bold mb-6">üü£ ClickUp Clone</h2>
    <nav className="flex flex-col gap-3">
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Dashboard</a>
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Tasks</a>
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Calendar</a>
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Settings</a>
    </nav>
  </div>
);

const initialProjects = {
  ProjectA: {
    todo: [],
    inprogress: [],
    done: [],
  },
};

const App = () => {
  const [projects, setProjects] = useState(initialProjects);
  const [activeProject, setActiveProject] = useState("ProjectA");
  const [isOpen, setIsOpen] = useState(false);
  const [editingTask, setEditingTask] = useState(null);
  const [activeColumn, setActiveColumn] = useState("todo");

  const [newTaskText, setNewTaskText] = useState("");
  const [description, setDescription] = useState("");
  const [dueDate, setDueDate] = useState("");
  const [subtasks, setSubtasks] = useState([]);
  const [newSubtask, setNewSubtask] = useState("");
  const [comments, setComments] = useState({});

  const tasks = projects[activeProject];

  const switchProject = (e) => setActiveProject(e.target.value);

  const openModal = (columnId, task = null) => {
    setActiveColumn(columnId);
    setIsOpen(true);
    if (task) {
      setEditingTask(task);
      setNewTaskText(task.content);
      setDescription(task.description || "");
      setDueDate(task.dueDate || "");
      setSubtasks(task.subtasks || []);
    } else {
      setEditingTask(null);
      setNewTaskText("");
      setDescription("");
      setDueDate("");
      setSubtasks([]);
    }
  };

  const closeModal = () => {
    setIsOpen(false);
    setNewTaskText("");
    setDescription("");
    setDueDate("");
    setSubtasks([]);
  };

  const handleTaskSubmit = () => {
    if (!newTaskText.trim()) return;
    const task = {
      id: editingTask ? editingTask.id : Date.now().toString(),
      content: newTaskText,
      description,
      dueDate,
      subtasks,
    };

    const updatedColumn = editingTask
      ? projects[activeProject][activeColumn].map((t) =>
          t.id === editingTask.id ? task : t
        )
      : [...projects[activeProject][activeColumn], task];

    const updatedProject = {
      ...projects[activeProject],
      [activeColumn]: updatedColumn,
    };

    setProjects({ ...projects, [activeProject]: updatedProject });
    closeModal();
  };

  const deleteTask = (columnId, taskId) => {
    const updatedColumn = projects[activeProject][columnId].filter(
      (t) => t.id !== taskId
    );
    setProjects({
      ...projects,
      [activeProject]: { ...projects[activeProject], [columnId]: updatedColumn },
    });
  };

  const toggleSubtask = (taskId, columnId, subIndex) => {
    const updatedColumn = projects[activeProject][columnId].map((task) => {
      if (task.id !== taskId) return task;
      const updatedSubs = task.subtasks.map((sub, i) =>
        i === subIndex ? { ...sub, done: !sub.done } : sub
      );
      return { ...task, subtasks: updatedSubs };
    });

    setProjects({
      ...projects,
      [activeProject]: { ...projects[activeProject], [columnId]: updatedColumn },
    });
  };

  const addComment = (taskId, columnId, commentText) => {
    const key = `${activeProject}-${columnId}-${taskId}`;
    const all = { ...comments };
    if (!all[key]) all[key] = [];
    all[key].push(commentText);
    setComments(all);
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const sourceCol = source.droppableId;
    const destCol = destination.droppableId;

    const sourceTasks = [...projects[activeProject][sourceCol]];
    const destTasks = [...projects[activeProject][destCol]];
    const [moved] = sourceTasks.splice(source.index, 1);

    if (sourceCol === destCol) {
      sourceTasks.splice(destination.index, 0, moved);
    } else {
      destTasks.splice(destination.index, 0, moved);
    }

    setProjects((prev) => ({
      ...prev,
      [activeProject]: {
        ...prev[activeProject],
        [sourceCol]: sourceTasks,
        [destCol]: destTasks,
      },
    }));
  };

  return (
    <div className="flex min-h-screen bg-gray-900 text-white">
      <Sidebar />
      <div className="flex-1 p-8 overflow-x-auto">
        <div className="flex justify-between mb-6">
          <h1 className="text-3xl font-bold">üóÇ Kanban Board</h1>
          <div className="flex gap-4">
            <select
              value={activeProject}
              onChange={switchProject}
              className="bg-gray-800 text-white border border-gray-600 rounded p-2"
            >
              {Object.keys(projects).map((proj) => (
                <option key={proj} value={proj}>
                  {proj}
                </option>
              ))}
            </select>
            <button
              onClick={() => {
                const newName = prompt("Enter new project name:");
                if (newName && !projects[newName]) {
                  setProjects({
                    ...projects,
                    [newName]: { todo: [], inprogress: [], done: [] },
                  });
                  setActiveProject(newName);
                }
              }}
              className="bg-purple-600 hover:bg-purple-500 px-4 rounded"
            >
              + New
            </button>
          </div>
        </div>

        <DragDropContext onDragEnd={onDragEnd}>
          <div className="grid grid-cols-3 gap-6">
            {Object.entries(tasks).map(([columnId, columnTasks]) => (
              <Droppable key={columnId} droppableId={columnId}>
                {(provided) => (
                  <div
                    {...provided.droppableProps}
                    ref={provided.innerRef}
                    className="bg-gray-800 rounded-lg p-4 min-h-[300px]"
                  >
                    <div className="flex justify-between mb-2">
                      <h2 className="capitalize font-bold">{columnId}</h2>
                      <button
                        onClick={() => openModal(columnId)}
                        className="bg-gray-700 hover:bg-gray-600 p-1 rounded"
                      >
                        <PlusIcon className="w-5 h-5" />
                      </button>
                    </div>

                    {columnTasks.map((task, index) => (
                      <Draggable key={task.id} draggableId={task.id} index={index}>
                        {(provided) => {
                          const key = `${activeProject}-${columnId}-${task.id}`;
                          return (
                            <div
                              ref={provided.innerRef}
                              {...provided.draggableProps}
                              {...provided.dragHandleProps}
                              className="bg-gray-700 rounded p-3 mb-3 shadow"
                            >
                              <div className="flex justify-between">
                                <div className="font-semibold">{task.content}</div>
                                <div className="text-sm space-x-2">
                                  <button onClick={() => openModal(columnId, task)}>‚úèÔ∏è</button>
                                  <button onClick={() => deleteTask(columnId, task.id)}>üóëÔ∏è</button>
                                </div>
                              </div>
                              <div className="text-sm text-gray-300 mt-1">{task.description}</div>
                              {task.dueDate && (
                                <div className="text-xs text-gray-400 mt-1">üìÖ Due: {task.dueDate}</div>
                              )}
                              {/* Subtasks */}
                              {task.subtasks?.length > 0 && (
                                <div className="mt-2 text-sm">
                                  <div className="font-medium">Subtasks:</div>
                                  {task.subtasks.map((sub, i) => (
                                    <label key={i} className="block">
                                      <input
                                        type="checkbox"
                                        checked={sub.done}
                                        onChange={() => toggleSubtask(task.id, columnId, i)}
                                        className="mr-2"
                                      />
                                      <span className={sub.done ? "line-through text-gray-400" : ""}>
                                        {sub.title}
                                      </span>
                                    </label>
                                  ))}
                                </div>
                              )}
                              {/* Comments */}
                              <div className="mt-3 text-sm">
                                <input
                                  type="text"
                                  className="w-full rounded bg-gray-800 text-white px-2 py-1 text-xs"
                                  placeholder="Add comment..."
                                  onKeyDown={(e) => {
                                    if (e.key === "Enter" && e.target.value.trim()) {
                                      addComment(task.id, columnId, e.target.value);
                                      e.target.value = "";
                                    }
                                  }}
                                />
                                <div className="mt-1 space-y-1 text-gray-300 text-xs">
                                  {(comments[key] || []).map((c, i) => (
                                    <div key={i}>üí¨ {c}</div>
                                  ))}
                                </div>
                              </div>
                            </div>
                          );
                        }}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </DragDropContext>
      </div>

      {/* Modal */}
      <Transition appear show={isOpen} as={Fragment}>
        <Dialog as="div" className="relative z-10" onClose={closeModal}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-200"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-150"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black/60" />
          </Transition.Child>

          <div className="fixed inset-0 overflow-y-auto">
            <div className="flex min-h-full items-center justify-center p-6">
              <Dialog.Panel className="w-full max-w-md rounded bg-gray-800 text-white p-6">
                <Dialog.Title className="text-lg font-bold mb-4">
                  {editingTask ? "Edit Task" : "Add Task"}
                </Dialog.Title>

                <input
                  value={newTaskText}
                  onChange={(e) => setNewTaskText(e.target.value)}
                  className="w-full p-2 mb-2 rounded bg-gray-700"
                  placeholder="Task title"
                />
                <textarea
                  value={description}
                  onChange={(e) => setDescription(e.target.value)}
                  className="w-full p-2 mb-2 rounded bg-gray-700"
                  placeholder="Description"
                />
                <input
                  type="date"
                  value={dueDate}
                  onChange={(e) => setDueDate(e.target.value)}
                  className="w-full p-2 mb-2 rounded bg-gray-700"
                />
                {/* Add subtasks */}
                <div className="mb-2">
                  <div className="font-medium mb-1">Subtasks</div>
                  {subtasks.map((s, i) => (
                    <div key={i} className="flex items-center mb-1">
                      <input
                        type="checkbox"
                        checked={s.done}
                        onChange={() =>
                          setSubtasks(
                            subtasks.map((sub, j) =>
                              j === i ? { ...sub, done: !sub.done } : sub
                            )
                          )
                        }
                        className="mr-2"
                      />
                      <span className={s.done ? "line-through text-gray-400" : ""}>
                        {s.title}
                      </span>
                    </div>
                  ))}
                  <div className="flex gap-2 mt-1">
                    <input
                      value={newSubtask}
                      onChange={(e) => setNewSubtask(e.target.value)}
                      className="flex-1 p-1 rounded bg-gray-700"
                      placeholder="New subtask"
                    />
                    <button
                      onClick={() => {
                        if (newSubtask.trim()) {
                          setSubtasks([...subtasks, { title: newSubtask, done: false }]);
                          setNewSubtask("");
                        }
                      }}
                      className="bg-purple-600 px-2 rounded"
                    >
                      Add
                    </button>
                  </div>
                </div>

                <div className="flex justify-end gap-2 mt-4">
                  <button onClick={closeModal} className="px-4 py-2 bg-gray-600 rounded">
                    Cancel
                  </button>
                  <button onClick={handleTaskSubmit} className="px-4 py-2 bg-purple-600 rounded">
                    {editingTask ? "Save" : "Add"}
                  </button>
                </div>
              </Dialog.Panel>
            </div>
          </div>
        </Dialog>
      </Transition>
    </div>
  );
};

export default App;
