import React, { useState, Fragment } from "react";
import {
  DragDropContext,
  Droppable,
  Draggable,
} from "react-beautiful-dnd";
import { Dialog, Transition } from "@headlessui/react";
import { PlusIcon } from "@heroicons/react/24/solid";

// Sidebar component
const Sidebar = () => (
  <div className="h-screen w-64 bg-gray-800 text-white flex flex-col p-4 space-y-4">
    <h2 className="text-xl font-bold mb-6">ðŸŸ£ ClickUp Clone</h2>
    <nav className="flex flex-col gap-3">
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Dashboard</a>
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Tasks</a>
      <a href="#" className="hover:bg-gray-700 px-3 py-2 rounded">Calendar</a>
    </nav>
  </div>
);

const initialProjects = {
  ProjectA: {
    todo: [],
    inprogress: [],
    done: [],
  },
};

const App = () => {
  const [projects, setProjects] = useState(initialProjects);
  const [activeProject, setActiveProject] = useState("ProjectA");
  const [isOpen, setIsOpen] = useState(false);
  const [activeColumn, setActiveColumn] = useState("todo");
  const [editingTask, setEditingTask] = useState(null);
  const [newTask, setNewTask] = useState({
    content: "",
    description: "",
    dueDate: "",
    subtasks: [],
    comments: [],
  });

  const tasks = projects[activeProject];

  const switchProject = (e) => setActiveProject(e.target.value);

  const openModal = (columnId, task = null) => {
    setActiveColumn(columnId);
    setIsOpen(true);
    if (task) {
      setEditingTask(task);
      setNewTask(task);
    } else {
      setEditingTask(null);
      setNewTask({
        content: "",
        description: "",
        dueDate: "",
        subtasks: [],
        comments: [],
      });
    }
  };

  const closeModal = () => {
    setNewTask({
      content: "",
      description: "",
      dueDate: "",
      subtasks: [],
      comments: [],
    });
    setIsOpen(false);
  };

  const handleChange = (field, value) => {
    setNewTask((prev) => ({ ...prev, [field]: value }));
  };

  const addSubtask = () => {
    setNewTask((prev) => ({
      ...prev,
      subtasks: [...prev.subtasks, { id: Date.now().toString(), title: "" }],
    }));
  };

  const handleSubtaskChange = (index, value) => {
    const updated = [...newTask.subtasks];
    updated[index].title = value;
    setNewTask((prev) => ({ ...prev, subtasks: updated }));
  };

  const addTask = () => {
    if (!newTask.content.trim()) return;

    const column = activeColumn;
    const updatedColumn = [...projects[activeProject][column]];

    if (editingTask) {
      const index = updatedColumn.findIndex((t) => t.id === editingTask.id);
      updatedColumn[index] = newTask;
    } else {
      updatedColumn.push({ ...newTask, id: Date.now().toString() });
    }

    const updatedProject = {
      ...projects[activeProject],
      [column]: updatedColumn,
    };

    setProjects({ ...projects, [activeProject]: updatedProject });
    closeModal();
  };

  const deleteTask = (columnId, taskId) => {
    const updatedTasks = projects[activeProject][columnId].filter(
      (t) => t.id !== taskId
    );
    const updatedProject = {
      ...projects[activeProject],
      [columnId]: updatedTasks,
    };
    setProjects({ ...projects, [activeProject]: updatedProject });
  };

  const onDragEnd = (result) => {
    const { source, destination } = result;
    if (!destination) return;

    const srcCol = source.droppableId;
    const destCol = destination.droppableId;

    const srcTasks = [...projects[activeProject][srcCol]];
    const destTasks = [...projects[activeProject][destCol]];
    const [moved] = srcTasks.splice(source.index, 1);

    if (srcCol === destCol) {
      srcTasks.splice(destination.index, 0, moved);
    } else {
      destTasks.splice(destination.index, 0, moved);
    }

    setProjects({
      ...projects,
      [activeProject]: {
        ...projects[activeProject],
        [srcCol]: srcTasks,
        [destCol]: destTasks,
      },
    });
  };

  const addProject = () => {
    const name = prompt("Enter new project name:");
    if (!name || projects[name]) return;
    setProjects({
      ...projects,
      [name]: { todo: [], inprogress: [], done: [] },
    });
    setActiveProject(name);
  };

  return (
    <div className="flex min-h-screen bg-gray-900 text-white">
      <Sidebar />
      <div className="flex-1 p-8 overflow-x-auto">
        {/* Header */}
        <div className="flex justify-between items-center mb-6">
          <h1 className="text-3xl font-bold">ðŸ—‚ Kanban Board</h1>
          <div className="flex items-center gap-4">
            <select
              value={activeProject}
              onChange={switchProject}
              className="bg-gray-800 text-white border border-gray-600 rounded p-2"
            >
              {Object.keys(projects).map((proj) => (
                <option key={proj} value={proj}>{proj}</option>
              ))}
            </select>
            <button
              onClick={addProject}
              className="bg-purple-600 px-4 py-2 rounded hover:bg-purple-500"
            >
              + New
            </button>
          </div>
        </div>

        {/* Board */}
        <DragDropContext onDragEnd={onDragEnd}>
          <div className="grid grid-cols-3 gap-6">
            {Object.entries(tasks).map(([columnId, columnTasks]) => (
              <Droppable key={columnId} droppableId={columnId}>
                {(provided) => (
                  <div
                    {...provided.droppableProps}
                    ref={provided.innerRef}
                    className="bg-gray-800 rounded-lg p-4 min-h-[300px]"
                  >
                    <div className="flex justify-between items-center mb-4">
                      <h2 className="text-lg font-semibold capitalize">{columnId}</h2>
                      <button
                        onClick={() => openModal(columnId)}
                        className="bg-gray-700 p-2 rounded hover:bg-gray-600"
                      >
                        <PlusIcon className="w-4 h-4" />
                      </button>
                    </div>
                    {columnTasks.map((task, index) => (
                      <Draggable key={task.id} draggableId={task.id} index={index}>
                        {(provided) => (
                          <div
                            {...provided.draggableProps}
                            {...provided.dragHandleProps}
                            ref={provided.innerRef}
                            className="bg-gray-700 p-3 rounded mb-3 shadow hover:bg-gray-600 relative"
                          >
                            <div className="text-white font-semibold">{task.content}</div>
                            {task.description && (
                              <div className="text-sm text-gray-300 italic">{task.description}</div>
                            )}
                            {task.dueDate && (
                              <div className="text-xs text-gray-400">ðŸ“… Due: {task.dueDate}</div>
                            )}
                            {task.subtasks?.length > 0 && (
                              <div className="text-xs text-gray-300 mt-2">
                                ðŸ§© {task.subtasks.length} Subtasks
                              </div>
                            )}
                            <div className="absolute top-1 right-1 flex gap-1 text-xs">
                              <button onClick={() => openModal(columnId, task)} className="text-yellow-300 hover:underline">Edit</button>
                              <button onClick={() => deleteTask(columnId, task.id)} className="text-red-400 hover:underline">Delete</button>
                            </div>
                          </div>
                        )}
                      </Draggable>
                    ))}
                    {provided.placeholder}
                  </div>
                )}
              </Droppable>
            ))}
          </div>
        </DragDropContext>
      </div>

      {/* Modal */}
      <Transition appear show={isOpen} as={Fragment}>
        <Dialog as="div" className="relative z-10" onClose={closeModal}>
          <Transition.Child
            as={Fragment}
            enter="ease-out duration-200"
            enterFrom="opacity-0"
            enterTo="opacity-100"
            leave="ease-in duration-150"
            leaveFrom="opacity-100"
            leaveTo="opacity-0"
          >
            <div className="fixed inset-0 bg-black/60" />
          </Transition.Child>

          <div className="fixed inset-0 flex items-center justify-center p-6">
            <Dialog.Panel className="bg-gray-800 p-6 rounded shadow w-full max-w-lg">
              <Dialog.Title className="text-lg font-bold mb-4">
                {editingTask ? "Edit Task" : "Add Task"} ({activeColumn})
              </Dialog.Title>
              <input
                value={newTask.content}
                onChange={(e) => handleChange("content", e.target.value)}
                placeholder="Task title"
                className="w-full p-2 rounded bg-gray-700 border border-gray-600 mb-3"
              />
              <textarea
                value={newTask.description}
                onChange={(e) => handleChange("description", e.target.value)}
                placeholder="Task description"
                className="w-full p-2 rounded bg-gray-700 border border-gray-600 mb-3"
              />
              <input
                type="date"
                value={newTask.dueDate}
                onChange={(e) => handleChange("dueDate", e.target.value)}
                className="w-full p-2 rounded bg-gray-700 border border-gray-600 mb-3"
              />
              {/* Subtasks */}
              <div className="mb-3">
                <h4 className="text-sm font-semibold mb-1">Subtasks:</h4>
                {newTask.subtasks.map((st, i) => (
                  <input
                    key={i}
                    value={st.title}
                    onChange={(e) => handleSubtaskChange(i, e.target.value)}
                    placeholder={`Subtask ${i + 1}`}
                    className="w-full mb-1 p-2 rounded bg-gray-700 border border-gray-600"
                  />
                ))}
                <button onClick={addSubtask} className="text-blue-400 text-sm hover:underline">+ Add Subtask</button>
              </div>

              <div className="flex justify-end gap-2">
                <button onClick={closeModal} className="bg-gray-600 px-4 py-2 rounded hover:bg-gray-500">Cancel</button>
                <button onClick={addTask} className="bg-purple-600 px-4 py-2 rounded hover:bg-purple-500">
                  {editingTask ? "Update" : "Add"}
                </button>
              </div>
            </Dialog.Panel>
          </div>
        </Dialog>
      </Transition>
    </div>
  );
};

export default App;
